import requests

url = 'https://api.miramace.com/v1/task/ffaa02bc-eed2-49ae-aa34-34b3c3ce551d'
headers = {
    'Authorization': 'Bearer YOUR_API_KEY'
}

# Optional query parameters
params = {
    'include_patient': True,
    'include_transcript': True,
    'include_recording': False
}

response = requests.get(url, headers=headers, params=params)
print(response.json())

const response = await fetch('https://api.miramace.com/v1/task/ffaa02bc-eed2-49ae-aa34-34b3c3ce551d?include_patient=true&include_transcript=true', {
  headers: {
    'Authorization': 'Bearer YOUR_API_KEY'
  }
});

const data = await response.json();
console.log(data);

